import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';

const EditSalesOrder = () => {
  const { id } = useParams();
  const navigate = useNavigate();

  const [order, setOrder] = useState({
    customerName: '',
    products: [],
    totalPrice: 0,
    status: 'Pending',
  });

  useEffect(() => {
    axios.get(`http://localhost:5000/api/sales-orders/${id}`)
      .then(response => setOrder(response.data))
      .catch(error => console.error('Error fetching order:', error));
  }, [id]);

  const handleSubmit = (event) => {
    event.preventDefault();

    axios.put(`http://localhost:5000/api/sales-orders/${id}`, order)
      .then(response => {
        console.log('Order updated:', response.data);
        alert('Order updated successfully');
        navigate('/sales-orders');
      })
      .catch(error => {
        console.error('Error updating order:', error);
        alert('Failed to update order');
      });
  };

  return (
    <div className="container mt-5">
      <h2 className="text-center mb-4">Edit Sales Order</h2>
      <form onSubmit={handleSubmit}>
        <div className="mb-3">
          <label className="form-label">Customer Name</label>
          <input
            type="text"
            value={order.customerName}
            onChange={(e) => setOrder({ ...order, customerName: e.target.value })}
            className="form-control"
            required
          />
        </div>

        <div className="mb-3">
          <label className="form-label">Products (comma-separated)</label>
          <input
            type="text"
            value={order.products}
            onChange={(e) => setOrder({ ...order, products: e.target.value.split(',') })}
            className="form-control"
            required
          />
        </div>

        <div className="mb-3">
          <label className="form-label">Total Price</label>
          <input
            type="number"
            value={order.totalPrice}
            onChange={(e) => setOrder({ ...order, totalPrice: e.target.value })}
            className="form-control"
            required
          />
        </div>

        <div className="mb-3">
          <label className="form-label">Order Status</label>
          <select
            className="form-control"
            value={order.status}
            onChange={(e) => setOrder({ ...order, status: e.target.value })}
          >
            <option value="Pending">Pending</option>
            <option value="Shipped">Shipped</option>
            <option value="Delivered">Delivered</option>
          </select>
        </div>

        <button type="submit" className="btn btn-primary w-100">Update Order</button>
      </form>
    </div>
  );
};

export default EditSalesOrder;
