import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';  // Import useNavigate
import axios from 'axios';  // Import axios


const AddProduct = () => {
  const navigate = useNavigate();  // Use useNavigate hook to handle navigation
  const [productName, setProductName] = useState('');

  const handleSubmit = () => {
    if (productName.trim()) {
      const newProduct = { name: productName };

      // Assuming you're using axios for making API requests
      axios.post('http://localhost:5000/api/products', newProduct)
        .then(() => {
          navigate('/products');  // Redirect to the products page after success
        })
        .catch(error => console.error('Error adding product:', error));
    } else {
      alert('Please enter a product name');
    }
  };

  return (
    <div>
      <input 
        type="text" 
        value={productName} 
        onChange={(e) => setProductName(e.target.value)} 
        placeholder="Product Name"
      />
      <button onClick={handleSubmit}>Add Product</button>
    </div>
  );
};

export default AddProduct;
