import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const AddSalesOrder = () => {
  const [customerName, setCustomerName] = useState('');
  const [products, setProducts] = useState([]);
  const [totalPrice, setTotalPrice] = useState(0);
  const [status, setStatus] = useState('Pending');
  const navigate = useNavigate();

  const handleSubmit = async (event) => {
    event.preventDefault();

    const orderData = {
      customerName,
      products,
      totalPrice,
      status,
      orderDate: new Date(),
    };

    try {
      const response = await axios.post('http://localhost:5000/api/sales-orders', orderData);
      console.log('Sales Order added:', response.data);
      alert('Order placed successfully');
      navigate('/sales-orders'); // Redirect to sales orders page
    } catch (error) {
      console.error('Error adding order:', error);
      alert('Failed to place order');
    }
  };

  return (
    <div className="container mt-5">
      <h2 className="text-center mb-4">Create Sales Order</h2>
      <form onSubmit={handleSubmit}>
        <div className="mb-3">
          <label className="form-label">Customer Name</label>
          <input
            type="text"
            value={customerName}
            onChange={(e) => setCustomerName(e.target.value)}
            className="form-control"
            required
          />
        </div>

        {/* Add a field for product selection */}
        <div className="mb-3">
          <label className="form-label">Products (comma-separated)</label>
          <input
            type="text"
            value={products}
            onChange={(e) => setProducts(e.target.value.split(','))}
            className="form-control"
            required
          />
        </div>

        {/* Calculate total price */}
        <div className="mb-3">
          <label className="form-label">Total Price</label>
          <input
            type="number"
            value={totalPrice}
            onChange={(e) => setTotalPrice(e.target.value)}
            className="form-control"
            required
          />
        </div>

        {/* Order status */}
        <div className="mb-3">
          <label className="form-label">Order Status</label>
          <select
            className="form-control"
            value={status}
            onChange={(e) => setStatus(e.target.value)}
          >
            <option value="Pending">Pending</option>
            <option value="Shipped">Shipped</option>
            <option value="Delivered">Delivered</option>
          </select>
        </div>

        <button type="submit" className="btn btn-primary w-100">Place Order</button>
      </form>
    </div>
  );
};

export default AddSalesOrder;
