import React from 'react';
import { BrowserRouter as Router, Routes, Route, Link } from 'react-router-dom';
import ProductList from './components/ProductList.js';
import AddProduct from './components/AddProduct.js';
import EditProduct from './components/EditProduct.js';
import SalesOrderList from './components/SalesOrderList.js';
import AddSalesOrder from './components/AddSalesOrder.js';
import EditSalesOrder from './components/EditSalesOrder.js';  // Import SalesOrderList Component
import 'bootstrap/dist/css/bootstrap.min.css'; // Importing Bootstrap

function App() {
  return (
    <Router>
      <div className="App">
        {/* Navigation Bar */}
        <nav className="navbar navbar-expand-lg navbar-light bg-light">
          <div className="container-fluid">
            <Link className="navbar-brand" to="/">Inventory System</Link>
            <div className="collapse navbar-collapse">
              <ul className="navbar-nav ms-auto">
                <li className="nav-item">
                  <Link className="nav-link" to="/">Product List</Link>
                </li>
                <li className="nav-item">
                  <Link className="nav-link" to="/add">Add Product</Link>
                </li>
                <li className="nav-item">
                  <Link className="nav-link" to="/sales-orders">Sales Orders</Link> {/* Link to Sales Orders */}
                </li>
                <li className="nav-item">
                  <Link className="nav-link" to="/add-orders">Add Sales Order</Link> {/* Link to Add Sales Order */}
                </li>
                <li className="nav-item">
                  <Link className="nav-link" to="/edit-orders">Edit Sales Order</Link> {/* Link to Edit Sales Order */}
                </li>
              </ul>
            </div>
          </div>
        </nav>

        {/* Main Content */}
        <div className="container mt-5">
          <Routes>
            {/* Define your routes here */}
            <Route path="/" element={<ProductList />} />
            <Route path="/add" element={<AddProduct />} />
            <Route path="/edit/:id" element={<EditProduct />} />
            <Route path="/sales-orders" element={<SalesOrderList />} /> {/* Sales Order List route */}
            <Route path="/add-orders" element={<AddSalesOrder />} /> {/* Add Sales Order route */}
            <Route path="/edit-orders" element={<EditSalesOrder />} /> {/* Edit Sales Order route */}
          </Routes>
        </div>
      </div>
    </Router>
  );
}

export default App;
