// src/components/SalesOrderList.js
import React from 'react';
import { Link } from 'react-router-dom';

const SalesOrderList = () => {
  // This is just a placeholder; later you can integrate API calls to fetch actual orders
  const salesOrders = [
    { id: 1, customer: 'Customer A', total: 100 },
    { id: 2, customer: 'Customer B', total: 200 },
  ];

  return (
    <div>
      <h2>Sales Orders</h2>
      {salesOrders.length > 0 ? (
        <ul className="list-group">
          {salesOrders.map((order) => (
            <li key={order.id} className="list-group-item d-flex justify-content-between align-items-center">
              <span>{order.customer} - ${order.total}</span>
              <Link to={`/sales-order/${order.id}`} className="btn btn-info btn-sm">View Order</Link>
            </li>
          ))}
        </ul>
      ) : (
        <p>No sales orders found.</p>
      )}
    </div>
  );
};

export default SalesOrderList;
